package Problems;

//Java program to get the character at the given index within the string.
public class Problem003 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myText = "Hello World";
		
		int poitionOfArray = 1;
		char[] myarraye = myText.toCharArray();
		
		System.out.println("The character at position "+poitionOfArray +" is "+myarraye[poitionOfArray -1] );
		
	}

}
